DROP PROCEDURE IF EXISTS `Coins`;
CREATE DEFINER=`root`@`localhost` PROCEDURE `Coins` ()  NO SQL
  SELECT coin
  FROM coin
  GROUP BY coin ORDER BY coin DESC LIMIT 10;
  
